let db;
let dbReq = indexedDB.open('myDatabase', 3);

function addStickyNote(db, message) {
    // Start a database transaction and get the notes object store
    let tx = db.transaction(['notes'], 'readwrite');
    let store = tx.objectStore('notes');

    // Put the sticky note into the object store
    // let note = { text: message, timestamp: Date.now() };
    // store.add(note);
    // Wait for the database transaction to complete
    // tx.oncomplete = function () { console.log('stored note!') }
    // tx.onerror = function (event) {
    //     alert('error storing note ' + event.target.errorCode);
    // }
    let req = store.get(10)
    req.onsuccess = (event) => {
        let note = event.target.result;

        if (note) {
            console.log(note);
        } else {
            console.log("note 1 not found")
        }
    }
}
dbReq.onupgradeneeded = function (event) {
    // Set the db variable to our database so we can use it!  
    db = event.target.result;

    // Create an object store named notes. Object stores
    // in databases are where data are stored.
    let notes = db.createObjectStore('notes', { autoIncrement: true, keyPath: "id" });
}
dbReq.onsuccess = function (event) {
    db = event.target.result;
    addStickyNote(db, 'Sloths are awesome!');
}

dbReq.onerror = function (event) {
    alert('error opening database ' + event.target.errorCode);
}
