import socketApp from "../socket";
import { Link } from "react-router-dom"
import PolicyOutlinedIcon from '@material-ui/icons/PolicyOutlined';
import DynamicFeedOutlinedIcon from '@material-ui/icons/DynamicFeedOutlined';
import ContactSupportOutlinedIcon from '@material-ui/icons/ContactSupportOutlined';
let socket = socketApp.getSocket();
export const handleFileUpload = (e, userId, id) => {
    let file = e.target.files;
    let reader = new FileReader()
    for (let i = 0; i < file.length; i++) {
        reader.readAsDataURL(file[i])
        reader.onloadend = () => {
            socket.emit("sendImageOff", { room: id, image: reader.result, userId })
        }
    }
}
export const messageLiImageRender = (value, i, imgUrl, myRef) => {
    return (
        <li
            ref={myRef}
            key={i}
            className={"messageImage" + value}>
            <input type="checkbox" id={i}></input>
            <label for={i}>
                <img src={imgUrl}></img>
            </label>
        </li>
    )
}
export const messageLiRender = (li, one, two, msgs, i, myRef) => {
    return (
        <li className={li}
            key={i}
            ref={myRef}
        >
            {msgs.length > 60 ?
                <div className={one}>
                    <span>{msgs}</span>
                </div> :
                <div className={two}>
                    <span>{msgs}</span>
                </div>
            })
        </li>
    )
}
export const executeScroll = (myRef) => {
    try {
        myRef.current.scrollIntoView()
    } catch (error) {
    }
}
export const NavbarRight = () => {
    let arr = window.location.href.split("/")
    console.log(arr[3] === "")
    return (
        <div className="navbar_right">
            <ul>
                <li className={arr[3] === "" ? "active_title inner_title" : "inner_title"}>
                    <Link className="message_icon" to="/">
                        <img src="data:image/png;base64," />
                    </Link>
                    <span className="title" id="m_title">
                        Tin nhắn
                    </span>
                </li>
                <li className={arr[3] === "news" ? "active_title inner_title" : "inner_title"}>
                    <Link className="home_icon icon_nav" to="/news">
                        <img src="data:image/png;base64," />
                    </Link>
                    <span className="title" id="h_title">
                        Trang chủ
                    </span>
                </li>
                <li className={arr[3] === "feedback" ? "active_title inner_title" : "inner_title"}>
                    <Link className="feedback_icon icon_nav" to="/feedback">
                        <svg xmlns="http://www.w3.org/2000/svg" x="0px" y="0px"
                            width="48" height="48"
                            viewBox="0 0 172 172"
                            style=" fill:#ffffff;"><g fill="none" fill-rule="nonzero" stroke="none" stroke-width="1" stroke-linecap="butt" stroke-linejoin="miter" stroke-miterlimit="10" stroke-dasharray="" stroke-dashoffset="0" font-family="none" font-weight="none" font-size="none" text-anchor="none" style="mix-blend-mode: normal"><path d="M0,172v-172h172v172z" fill="none"></path><g fill="#ffffff"><path d="M139.75,35.83333h-100.33333c-9.89717,0 -17.91667,8.0195 -17.91667,17.91667v9.202c0,4.02767 -1.6985,7.869 -4.67267,10.58517l-3.58333,3.27517c-5.40367,4.93067 -5.40367,13.4375 0,18.36817l3.58333,3.27517c2.97417,2.71617 4.67267,6.5575 4.67267,10.59233v9.202c0,9.89717 8.0195,17.91667 17.91667,17.91667h100.33333c9.89717,0 17.91667,-8.0195 17.91667,-17.91667v-64.5c0,-9.89717 -8.0195,-17.91667 -17.91667,-17.91667zM100.33333,107.5h-43c-3.94167,0 -7.16667,-3.225 -7.16667,-7.16667c0,-3.94167 3.225,-7.16667 7.16667,-7.16667h43c3.94167,0 7.16667,3.225 7.16667,7.16667c0,3.94167 -3.225,7.16667 -7.16667,7.16667zM121.83333,78.83333h-64.5c-3.94167,0 -7.16667,-3.225 -7.16667,-7.16667c0,-3.94167 3.225,-7.16667 7.16667,-7.16667h64.5c3.94167,0 7.16667,3.225 7.16667,7.16667c0,3.94167 -3.225,7.16667 -7.16667,7.16667z" opacity="0.35"></path><circle cx="8" cy="2" transform="scale(7.16667,7.16667)" r="1"></circle><circle cx="16" cy="2" transform="scale(7.16667,7.16667)" r="1"></circle><circle cx="12" cy="1" transform="scale(7.16667,7.16667)" r="1"></circle><circle cx="8" cy="22" transform="scale(7.16667,7.16667)" r="1"></circle><circle cx="16" cy="22" transform="scale(7.16667,7.16667)" r="1"></circle><circle cx="12" cy="23" transform="scale(7.16667,7.16667)" r="1"></circle><path d="M121.83333,78.83333h-64.5c-3.956,0 -7.16667,-3.21067 -7.16667,-7.16667v0c0,-3.956 3.21067,-7.16667 7.16667,-7.16667h64.5c3.956,0 7.16667,3.21067 7.16667,7.16667v0c0,3.956 -3.21067,7.16667 -7.16667,7.16667z"></path><path d="M100.33333,107.5h-43c-3.956,0 -7.16667,-3.21067 -7.16667,-7.16667v0c0,-3.956 3.21067,-7.16