import React, { useState, useRef, useEffect } from 'react'
import Dropzone from 'react-dropzone'
import Modal from '@material-ui/core/Modal';
import Backdrop from '@material-ui/core/Backdrop';
import Fade from '@material-ui/core/Fade';
import CreateOutlinedIcon from '@material-ui/icons/CreateOutlined';
import CloudUploadIcon from '@material-ui/icons/CloudUpload';
import axios from "axios";
import { getCookie } from '../auth';
import RequireLogin from './requireLogin';
import { IconButton, Menu } from '@material-ui/core';
import { toast } from "react-toastify"
// import { emoji } from "../../helpers/emoji"
import { Picker } from "emoji-mart"
import 'emoji-mart/css/emoji-mart.css'


function Index() {
    const [text, setText] = useState("")
    const [file, setFile] = useState([])
    const [url, setUrl] = useState([])
    // const [dataFile, setDataFile] = useState("")
    const [open, setOpen] = useState(false)
    const [login, setLogin] = useState(false)
    const [anchorEl, setAnchorEl] = useState(null)
    const id = getCookie().token;
    const inputRef = useRef(null)
    const [cursorPosition, setCursorPosition] = useState()

    const dragFile = (file) => {
        let data = file[0];
        var URL = window.URL || window.webkitURL;
        var src = URL.createObjectURL(data);
        setUrl(value => [...value, src])
        setFile(value => [...value, data])
    }
    const handleSubmit = (e) => {
        e.preventDefault();
        let formData = new FormData()
        formData.append("text", text)
        formData.append("id", id)
        for (let value of file) {
            formData.append("files", value)
        }
        text && axios.post("http://localhost:2704/api/news/post", formData)
            .then(data => {
                toast.success("Bài viết của ban đã được đăng")
                handleClose()
            }).catch(err => {
                console.log(err)
            })
    }
    const handleOpen = () => {
        if (!id) {
            setLogin(true)
        } else {
            setOpen(true);
        }

    };
    const handleClose = () => {
        setOpen(false);
    };
    const handleDeleteImamge = (index) => {
        const arr = [...file]
        const brr = [...url]
        arr.splice(index, 1)
        brr.splice(index, 1)
        setFile(arr)
        setUrl(brr)
    }
    const handleChangeText = e => {
        setText(e)
    }
    const handleLineBreak = e => {
        if (e.key === "Enter") {
            let value = text + "<br/>"
            setText(value)
        }
    }
    const handleOpenEmo = (event) => {
        setAnchorEl(event.currentTarget);
    };

    const handleCloseEmo = () => {
        setAnchorEl(null);
    };
    const emojiSubmit = (e, emo) => {
        if (inputRef.current) {
            const ref = inputRef.current
            ref.focus()
            const start = text.substring(0, ref.selectionStart)
            const end = text.substring(ref.selectionEnd)
            const message = start + emo.native + end
            setText(message)
            setCursorPosition(start.length + emo.native.length)
        }
        handleCloseEmo()
    }
    useEffect(() => {
        inputRef.current ? inputRef.current.selectionEnd = cursorPosition : console.log()
    }, [cursorPosition])

    return (
        <div className="post_data ">
            {login === true ? <RequireLogin onClick={(value) => setLogin(value)} /> : console.log()}
            <button className="post_data_button tooltip" type="button" onClick={handleOpen}>
                <CreateOutlinedIcon />
                <span className="tooltiptext" id="tooltip_post">
                    Biểu tượng
                </span>
            </button>

            <Modal
                className="modal_post_blog"
                aria-labelledby="transition-modal-title"
                aria-describedby="transition-modal-description"
                // className={classes.modal}
                open={open}
                onClose={handleClose}
                closeAfterTransition
                BackdropComponent={Backdrop}
                BackdropProps={{
                    timeout: 500,
                }}
            >
                <Fade in={open}>
                    <form action="#" onSubmit={handleSubmit} className="form_post_blog" >
                        <div className="close_form_button">
                            <IconButton onClick={handleClose}>
                                x
                            </IconButton>
                        </div>
                        <h4>Đăng tải </h4>
                        <div id="form_post_blog_div">
                            <div className="input_extension">
                                <IconButton aria-controls="simple_menu_emoji" aria-haspopup="true" onClick={handleOpenEmo}>
                                    <img alt="emoji_image" src="data:image/png;base64," />
                                </IconButton>
                                <IconButton>
                                    <img alt="setting_image" src="data:image/png;base64," />
                                </IconButton>
                                <IconButton>
                                </IconButton>
                                <Menu
                                    id="simple_menu_emoji"
                                    anchorEl={anchorEl}
                                    keepMounted
                                    open={Boolean(anchorEl)}
                                    onClose={handleCloseEmo}>
                                    <Picker onSelect={e => emojiSubmit(1, e)} set={"facebook"} theme={"dark"} showPreview={false} showSkinTones={false} />
                                </Menu>
                            </div>
                            <textarea
                                ref={inputRef}
                                value={text}
                                id="type_text_div"
                                onInput={e => handleChangeText(e.target.value)} >
                            </textarea>
                        </div>
                        <Dropzone
                            className="inner_title"
                            onDrop={acceptedFiles => dragFile(acceptedFiles)}>
                            {({ getRootProps, getInputProps }) => (
                                <section >
                                    <div className="drag_file" {...getRootProps()}>
                                        <input type="file" accept="image/*"  {...getInputProps()} />
                                        <div className="icon">
                                            <CloudUploadIcon />
                                        </div>
                                    </div>
                                </section>

                            )}
                        </Dropzone>
                        <div>
                            <div className="image_post_news">
                                {
                                    typeof url !== 'undefined' ? url.map(function (value, index) {
                                        return <li key={index}>
                                            <img alt="image_upload" src={value}></img>
                                            <span onClick={() => handleDeleteImamge(index)}>&#10005;</span>
                                        </li>
                                    }) : {}}
                            </div>
                        </div>
                        <IconButton id="upload_submit_button" type="submit">
                            <img alt="upload_image" src="data:image/png;base64," />
                        </IconButton>
                    </form>
                </Fade>
            </Modal>
        </div>
    )
}

export default Index
