import socketApp from "../socket";
import { Link } from "react-router-dom"
import PolicyOutlinedIcon from '@material-ui/icons/PolicyOutlined';
import DynamicFeedOutlinedIcon from '@material-ui/icons/DynamicFeedOutlined';
import ContactSupportOutlinedIcon from '@material-ui/icons/ContactSupportOutlined';
import HomeIcon from '@material-ui/icons/Home';
import MessageIcon from '@material-ui/icons/Message';
let socket = socketApp.getSocket();
export const handleFileUpload = (e, userId, id) => {
    let file = e.target.files;
    let reader = new FileReader()
    for (let i = 0; i < file.length; i++) {
        reader.readAsDataURL(file[i])
        reader.onloadend = () => {
            socket.emit("sendImageOff", { room: id, image: reader.result, userId })
        }
    }
}
export const messageLiImageRender = (value, i, imgUrl, myRef) => {
    return (
        <li
            ref={myRef}
            key={i}
            className={"messageImage" + value}>
            <input type="checkbox" id={i}></input>
            <label for={i}>
                <img src={imgUrl}></img>
            </label>
        </li>
    )
}
export const messageLiRender = (li, one, two, msgs, i, myRef) => {
    return (
        <li className={li}
            key={i}
            ref={myRef}
        >
            {msgs.length > 60 ?
                <div className={one}>
                    <span>{msgs}</span>
                </div> :
                <div className={two}>
                    <span>{msgs}</span>
                </div>
            })
        </li>
    )
}
export const executeScroll = (myRef) => {
    try {
        myRef.current.scrollIntoView()
    } catch (error) {
    }
}
export const NavbarRight = () => {
    let arr = window.location.href.split("/")
    console.log(arr[3] === "")
    return (
        <div className="navbar_right">
            <ul>
                <li className={arr[3] === "" ? "active_title inner_title" : "inner_title"}>
                    <Link className="message_icon" to="/">
                        <img src="data:image/png;base64," />
                    </Link>
                    <span className="title" id="m_title">
                        Tin nhắn
                    </span>
                </li>
                <li className={arr[3] === "news" ? "active_title inner_title" : "inner_title"}>
                    <Link className="home_icon icon_nav" to="/news">
                        <svg xmlns="http://www.w3.org/2000/svg" x="0px" y="0px"
                            width="64" height="64"
                            viewBox="0 0 172 172"
                        ><g fill="none" fill-rule="nonzero" stroke="none" stroke-width="1" stroke-linecap="butt" stroke-linejoin="miter" stroke-miterlimit="10" stroke-dasharray="" stroke-dashoffset="0" font-family="none" font-weight="none" font-size="none" text-anchor="none" style="mix-blend-mode: normal"><path d="M0,172v-172h172v172z" fill="none"></path><g><path d="M2.65391,86c0,-46.02344 37.32266,-83.34609 83.34609,-83.34609c46.02344,0 83.34609,37.32266 83.34609,83.34609c0,46.02344 -37.32266,83.34609 -83.34609,83.34609c-46.02344,0 -83.34609,-37.32266 -83.34609,-83.34609z" fill="#333333"></path><path d="M137.09609,69.47188l-50.42422,-25.16172c-0.43672,-0.20156 -0.90703,-0.20156 -1.34375,0l-50.45781,25.16172c-0.57109,0.26875 -0.90703,0.80625 -0.90703,1.44453v9.94375c0,0.5375 0.26875,1.04141 0.73906,1.34375c0.47031,0.30234 1.04141,0.33594 1.51172,0.10078l49.78594,-24.85938l49.75234,24.85938c0.20156,0.10078 0.43672,0.16797 0.67188,0.16797c0.30234,0 0.60469,-0.06719 0.83984,-0.23516c0.47031,-0.30234 0.73906,-0.80625 0.73906,-1.34375v-10.01094c0,-0.60469 -0.33594,-1.17578 -0.90703,-1.41094z" fill="#ffffff"></path><path d="M127.82422,82.57344l-41.82422,-20.82812l-41.82422,20.82812v38.63281h23.17969v-29.62969h15.95703v29.62969h44.51172z" fill="#ffffff"></path><path d="M88.58672,91.57656h10.75v10.75h-10.75z" fill="#000000"></path></g></g></svg>                         </Link>
                    <span className="title" id="h_title">
                        Trang chủ
                    </span>
                </li>
                <li className={arr[3] === "feedback" ? "active_title inner_title" : "inner_title"}>
                    <Link className="feedback_icon icon_nav" to="/feedback">
                        <DynamicFeedOutlinedIcon />
                    </Link>
                    <span className="title" id="f_title">
                        Góp ý và cải thiện
                    </span>
                </li>
                <li className={arr[3] === "help" ? "active_title inner_title" : "inner_title"}>
                    <Link className="contact_icon icon_nav" to="/help">
                        <ContactSupportOutlinedIcon />
                    </Link>
                    <span className="title" id="c_title">
                        Trợ giúp
                    </span>
                </li>
                <li className={arr[3] === "policy" ? "active_title inner_title" : "inner_title"}>
                    <Link className="policy_icon icon_nav" to="/policy">
                        <PolicyOutlinedIcon />
                    </Link>
                    <span className="title" id="p_title">
                        Điều khoản và dịch vụ
                    </span>
                </li>
            </ul>
        </div>
    )
}
