import React, { useState, useEffect } from 'react'
import Chat from './chat.js';
import { useHistory } from 'react-router-dom';
import ContactContain from './miniChatCom/contactContain.js';
import { getCookie } from '../../helpers/auth.js';
import axios from "axios"
import SendContact from './sendContact.js';
import { HeaderPage, NavbarRight } from '../../helpers/news.js';
import socketApp from '../../../socket';
var socket = socketApp.getSocket();


function Main_page() {
    const history = useHistory()
    const [room, setRoom] = useState("");
    const id = getCookie().token;
    const [start, setStart] = useState(0);
    const [end, setEnd] = useState(10);
    //value contain idRooms
    const [value, setValue] = useState([]);
    const [name, setName] = useState("Recal")
    // user contain 2 user 
    const [user, setUser] = useState("")

    let lu;

    useEffect(() => {
        axios.post("http://localhost:2704/api/msgC/contactL?start=" + start + "&end=" + end, { id })
            .then(res => {
                if (res.data.message) {
                    history.push("/report")
                } else {
                    setValue(val => [...val, res.data])
                }
            }).catch(err => {
            })
        socket.on("message", msgs => {
            // if (msgs.idRoom === idRoom) {
            lu = msgs.user
            let a = msgs.text ? decryptWithAES(msgs.text) : ""
            if (lu === id) sliceMess(a, "Bạn: ")
            else {
                sliceMess(a, "Đằng ấy: ")
                // arr[2] contain true or false read
                if (message[2] === "false") {
                    setRead(true)
                }
            }
            // }
        })
    }, [start])
    const hanldeSetRoom = value => {
        let id = value.split(",")
        setRoom(id[0])
        setUser(id[1])
    }

    return (
        <div className="main_page_auth">
            <HeaderPage />
            <div className="container">
                <NavbarRight />
                {
                    // id room id not exists than render chat component
                    room ? <SendContact onClick={(value) => hanldeSetRoom(value)} id={room} name={(value) => setName(value)} /> : <Chat />
                }
                <div className="contact" >
                    {
                        // if value have then we can use this 
                        value.length > 0 ?
                            value[0].map((val, i) => <div key={i}>
                                <ContactContain
                                    onClick={(value) => hanldeSetRoom(value)}
                                    message={val.data}
                                    users={val.user}
                                    idRoom={val.idRoom}
                                    target={room}
                                />
                            </div>)
                            : console.log()
                    }
                </div>
            </div>
        </div >
    )
}

export default Main_page
