import React, { useRef, useState } from 'react'
import { Link } from "react-router-dom"
import axios from "axios"
import imageCompression from 'browser-image-compression';
import TextareaAutosize from 'react-textarea-autosize';


function CreateShots() {
    const fileRef = useRef(null);
    const [file, setFile] = useState(null)
    const [text, setText] = useState("")
    const [color, setColor] = useState("#ffffff")
    const [fontSize, setFontSize] = useState(16)

    const handleFileUpload = async (e) => {
        let imageFile = e.target.files[0];
        const options = {
            maxSizeMB: 1,
            maxWidth: 400,
            useWebWorker: true
        }
        try {
            const compressedFile = await imageCompression(imageFile, options);
            var urlCreator = window.URL || window.webkitURL;
            setFile(urlCreator.createObjectURL(compressedFile))
            // await uploadToServer(compressedFile); // write your own logic
        } catch (error) {
            console.log(error);
        }
    }
    const handleChangeNumber = (e) => {
        if (parseInt(e.target.value) > 50) setFontSize(50)
        else if (parseInt(e.target.value) < 0) setFontSize(1)
        else setFontSize(parseInt(e.target.value) || 16)
    }
    return (
        <div className="story">
            <div className="story-header">
                <Link style={{ marginLeft: "10px", marginRight: "10px" }} to="/">
                    x
                </Link>
                <p>Brand</p>
            </div>
            <div className="story-center">
                <div className="option-1">
                    <p style={{ fontSize: "1.3rem" }}>Thoi gian xuat hien: </p>
                    <div className="time">
                        <input min="0" max="24" placeholder="Gio" type="number"></input>
                        <span>:</span>
                        <input min="0" max="60" placeholder="Phut" type="number"></input>
                    </div>
                    <div className="text">
                        <p>Noi dung</p>
                        <TextareaAutosize
                            value={text}
                            onChange={e => setText(e.target.value)}
                        />
                    </div>
                </div>
                <div className="story-content">
                    <div className="story_content_div" style={file ? { backgroundImage: "url(" + file + ")" } : console.log()}>
                        <div>
                            {text.split(/\r\n|\r|\n/).map(function (value) {
                                return <p style={{ color: color, fontSize: fontSize || 15 }} > {value}</p>
                            })}
                        </div>
                    </div>
                </div>
                <div className="option-2">
                    <div className="header">
