import React, { useState } from 'react'
import { Link } from "react-router-dom"
import { MenuItem, Menu, Button } from '@material-ui/core';
import NotificationsIcon from '@material-ui/icons/Notifications';
import ExitToAppIcon from '@material-ui/icons/ExitToApp';
import { signOut } from './auth';
import IconButton from '@material-ui/core/IconButton';
import Resizer from 'react-image-file-resizer';


export const resizeImage = (file, w, h, type) => new Promise(resolve => {
    console.log(file)
    Resizer.imageFileResizer(file, w, h, type, 100, 0,
        uri => {
            resolve(uri);
        },
        'base64'
    );
})


export const ImageRender = (cb) => {
    let { value } = cb.props
    value.image.map(async function (data) {
        let img = await resizeImage(data, 350, 400, "JPEG")
        console.log(img)
        return img
    })

    switch (value.image.length) {
        case 0:
            return <div></div>

        case 1:
            return <div className="one">
                <Link to={"/posts/id=" + value.idBlog}>
                    <img
                        alt={value.image[0].slice(100, 110)}
                        style={{
                            borderTopLeftRadius: "25px",
                            borderBottomLeftRadius: "25px",
                            borderTopRightRadius: "25px",
                            borderBottomRightRadius: "25px"
                        }}
                        src={value.image[0]} />
                </Link>
            </div>
        case 2:
            return <div className="two">
                <Link to={"/posts/id=" + value.idBlog}>
                    <img
                        alt={value.image[0].slice(100, 110)}
                        style={{
                            borderTopLeftRadius: "25px",
                            borderBottomLeftRadius: "25px"
                        }}
                        src={value.image[0]}></img>
                    <img
                        alt={value.image[1].slice(100, 110)}
                        style={{
                            borderTopRightRadius: "25px",
                            borderBottomRightRadius: "25px"
                        }}
                        src={value.image[1]}></img>
                </Link>
            </div>

        case 3:
            return <div className="three">
                <Link to={"/posts/id=" + value.idBlog}>
                    <div className="two">
                        <img
                            alt={value.image[0].slice(100, 110)}
                            style={{
                                borderTopLeftRadius: "25px"
                            }}
                            src={value.image[0]}></img>
                        <img
                            style={{
                                borderTopRightRadius: "25px"
                            }}
                            alt={value.image[1].slice(100, 110)}
                            src={value.image[1]}></img>
                    </div>
                    <img
                        alt={value.image[2].slice(100, 110)}
                        style={{
                            borderBottomLeftRadius: "25px",
                            borderBottomRightRadius: "25px"
                        }}
                        src={value.image[2]}></img>
                </Link>
            </div>

        case 4:
            return <div className="three">
                <Link to={"/posts/id=" + value.idBlog}>
                    <div className="two">
                        <img
                            alt={value.image[0].slice(100, 110)}
                            style={{
                                borderTopLeftRadius: "25px",
                            }}
                            src={value.image[0]}></img>
                        <img
                            alt={value.image[1].slice(100, 110)}
                            style={{
                                borderTopRightRadius: "25px",
                            }}
                            src={value.image[1]}></img>
                    </div>
                    <div className="two">
                        <img
                            alt={value.image[2].slice(100, 110)}
                            style={{
                                borderBottomLeftRadius: "25px",
                            }}
                            src={value.image[2]}></img>
                        <img
                            alt={value.image[3].slice(100, 110)}
                            style={{
                                borderBottomRightRadius: "25px",
                            }}
                            src={value.image[3]}></img>
                    </div>
                </Link>
            </div>
        default:
            return <div className="three">
                <Link to={"/posts/id=" + value.idBlog}>
                    <div className="two">
                        <img
                            alt={value.image[0].slice(100, 110)}
                            style={{
                                borderTopLeftRadius: "25px",
                            }}
                            src={value.image[0]}></img>
                        <img
                            alt={value.image[0].slice(10, 100)}
                            style={{
                                borderTopRightRadius: "25px",
                            }}
                            src={value.image[1]}></img>
                    </div>
                    <div className="two" >
                        <img
                            alt={value.image[1].slice(100, 110)}
                            style={{
                                borderBottomLeftRadius: "25px",
                            }}
                            src={value.image[2]}></img>
                        <div style={{ position: "relative", width: "90%" }}>
                            <img
                                alt={value.image[2].slice(100, 110)}
                                style={{
                                    borderBottomRightRadius: "25px",
                                }}
                                className="more_image"
                                src={value.image[3]}></img>
                            <span className="last_image">
                                + {value.image.length - 4}
                            </span>
                        </div>
                    </div>
                </Link>
            </div>
    }
}

export const HeaderPage = (props) => {
