                            <NotificationsIcon />
                        </Button>
                        <span className="title" id="notify_title">
                            Thông báo
                        </span>
                    </div>
                    <Menu
                        id="menu_main_page"
                        anchorEl={openNoti}
                        keepMounted
                        open={Boolean(openNoti)}
                        onClose={handleCloseNoti}
                    >
                        <p>Thông báo</p>
                        {
                            value.map(function (data, i) {
