import socketApp from "../socket";
let socket = socketApp.getSocket();
export const handleFileUpload = (e, userId, id) => {
    let file = e.target.files;
    let reader = new FileReader()
    for (let i = 0; i < file.length; i++) {
        reader.readAsDataURL(file[i])
        reader.onloadend = () => {
            socket.emit("sendImageOff", { room: id, image: reader.result, userId })
        }
    }
}
export const messageLiImageRender = (value, i, imgUrl, myRef) => {
    return (
        <li
            ref={myRef}
            key={i}
            className={"messageImage" + value}>
            <input type="checkbox" id={i}></input>
            <label for={i}>
                <img alt="" src={imgUrl}></img>
            </label>
        </li>
    )
}
export const messageLiRender = (li, one, two, msgs, i, myRef) => {
    return (
        <li className={li}
            key={i}
            ref={myRef}
        >
            {msgs.length > 60 ?
                <div className={one}>
                    <span>{msgs}</span>
                </div> :
                <div className={two}>
                    <span>{msgs}</span>
                </div>
            })
        </li>
    )
}
export const executeScroll = (myRef) => {
    try {
        myRef.current.scrollIntoView()
    } catch (error) {
    }
}
export const Spinning = () => {
    return (
        <div className="scene">
            <svg
                version="1.1"
                id="dc-spinner"
                xmlns="http://www.w3.org/2000/svg"
                x="0px" y="0px"
                // width:"38"  height:"38"
                viewBox="0 0 38 38"
                preserveAspectRatio="xMinYMin meet"
            >
                <path fill="#2AA198" stroke="#2AA198" stroke-width="0.6027" strokeMiterlimit="10" d="M5.203,20
			c0-8.159,6.638-14.797,14.797-14.797V5C11.729,5,5,11.729,5,20s6.729,15,15,15v-0.203C11.841,34.797,5.203,28.159,5.203,20z">
                    <animateTransform
                        attributeName="transform"
                        type="rotate"
                        from="0 20 20"
                        to="360 20 20"
                        dur="0.5s"
                        repeatCount="indefinite" />
                </path>
            </svg>
        </div >
    )
}
