import React, { useState } from 'react'
import Dropzone from 'react-dropzone'
import Modal from '@material-ui/core/Modal';
import Backdrop from '@material-ui/core/Backdrop';
import Fade from '@material-ui/core/Fade';
import CreateOutlinedIcon from '@material-ui/icons/CreateOutlined';
import CloudUploadIcon from '@material-ui/icons/CloudUpload';
import axios from "axios";
import { getCookie } from '../../../helpers/auth';
import RequireLogin from '../../../helpers/requireLogin';
import { emoji } from '../../../helpers/emoji';
import { IconButton, Menu } from '@material-ui/core';
import { toast } from "react-toastify"
import { ContentEditable } from '../../../helpers/news';


function Index() {
    const [text, setText] = useState("")
    const [file, setFile] = useState([])
    const [open, setOpen] = useState(false)
    const [login, setLogin] = useState(false)
    const [anchorEl, setAnchorEl] = useState(null)
    let runAfter = ContentEditable()
    const id = getCookie().token;

    const dragFile = (file) => {
        let data = file[0];
        let reader = new FileReader()
        reader.readAsDataURL(data)
        reader.onloadend = () => {
            setFile(value => [...value, reader.result])
        }
    }
    const handleSubmit = (e) => {
        e.preventDefault();
        text && axios.post("http://localhost:2704/api/news/post", { text, file, id })
            .then(data => {
                toast.success("Bài viết của ban đã được đăng")
                handleClose()
            }).catch(err => {
                console.log(err)
            })
    }
    const handleOpen = () => {
        if (!id) {
            setLogin(true)
        } else {
            setOpen(true);
        }

    };
    const handleClose = () => {
        setOpen(false);
    };
    const handleDeleteImamge = (index) => {
        const arr = [...file]
        arr.splice(index, 1)
        setFile(arr)
    }
    const handleChangeText = e => {
        setText(e.target.value)
    }
    const handleLineBreak = e => {
        if (e.key === "Enter") {
            let value = text + "<br/>"
            setText(value)
        }
    }
    const handleOpenEmo = (event) => {
        setAnchorEl(event.currentTarget);
    };

    const handleCloseEmo = () => {
        setAnchorEl(null);
    };
    const emojiSubmit = (e, emo) => {
        let value = text + " &#" + emo + ";"
        setText(value)
        handleCloseEmo()
    }

    return (
        <div className="post_data inner_title">
            {login === true ? <RequireLogin onClick={(value) => setLogin(value)} /> : console.log()}
            <button className="post_data_button" type="button" onClick={handleOpen}>
                <CreateOutlinedIcon />
            </button>
            <span className="title" id="post_blog_title">
                Đăng tải bài viết của bạn
                </span>
            <Modal
                className="modal_post_blog"
                aria-labelledby="transition-modal-title"
                aria-describedby="transition-modal-description"
                // className={classes.modal}
                open={open}
                onClose={handleClose}
                closeAfterTransition
                BackdropComponent={Backdrop}
                BackdropProps={{
                    timeout: 500,
                }}
            >
                <Fade in={open}>
                    <form onSubmit={handleSubmit} className="form_post_blog">
                        <h4>Đăng tải nội dung của bạn</h4>
                        <div id="form_post_blog_div">
                            <div className="input_extension">
                                <IconButton aria-controls="simple_menu_emoji" aria-haspopup="true" onClick={handleOpenEmo}>
                                    <img alt="emoji_image" src="data:image/png;base64," />
                                </IconButton>
                                <Menu
                                    id="simple_menu_emoji"
                                    anchorEl={anchorEl}
                                    keepMounted
                                    open={Boolean(anchorEl)}
                                    onClose={handleCloseEmo}
                                >
                                    {emoji.map(function (emo, i) {
                                        return (<span key={i} onClick={(e) => emojiSubmit(e, emo)} dangerouslySetInnerHTML={{ __html: "&#" + emo + ";" }}></span>)
                                    })}
                                </Menu>
                            </div>
                            <textarea
                                value={text}
                                // dangerouslySetInnerHTML={{ __html: text }}
                                // contentEditable={true}
                                // onKeyPress={handleLineBreak}
                                id="type_text_div"
                                onChange={handleChangeText} >
                                {console.log(text)}

                            </textarea>
                        </div>

                        <Dropzone
                            className="inner_title"
                            onDrop={acceptedFiles => dragFile(acceptedFiles)}>
                            {({ getRootProps, getInputProps }) => (
                                <section >
                                    <div className="drag_file" {...getRootProps()}>
                                        <input {...getInputProps()} />
                                        <div className="icon">
                                            <CloudUploadIcon />
                                        </div>
                                    </div>
                                </section>

                            )}
                        </Dropzone>
                        <div>
                            <div className="image_post_news">
                                {
                                    typeof file !== 'undefined' ? Object.values(file).map(function (value, index) {
                                        return <li key={index}>
                                            <img alt="image_upload" src={value}></img>
                                            <span onClick={() => handleDeleteImamge(index)}>&#10005;</span>
                                        </li>
                                    }) : {}}
                            </div>
                        </div>
                        <button id="upload_submit_button" type="submit">
                            <img alt="upload_image" src="data:image/png;base64," />
                        </button>
                    </form>
                </Fade>
            </Modal>
        </div>
    )
}

export default Index
